import React, { Component } from "react";
import { Card, Icon, Image, Button, Modal, Header, Input, TextArea, Container } from 'semantic-ui-react'
import { Link } from "react-router-dom";

const data = {
  user: {
    id: 498,
    username: "SD",
    displayName: "Salvador Degla",
    about: "Cool dude. Awesome coder. Located in Indy.",
    createdAt: "2019-03-12T16:01:39.622Z",
    updatedAt: "2019-03-12T16:01:39.622Z",
    messages: []
  }
};
class UserProfile extends Component {
  render() {
    return (
      <Card centered= 'true'>
      <Image wrapped size='medium' src='https://react.semantic-ui.com/images/avatar/large/matthew.png' />
    <Card.Content>
      <Card.Header>{data.user.username}</Card.Header>
      <Card.Meta>
        <span className='Username'>{data.user.displayName}</span>
      </Card.Meta>
      <Icon name='drivers license' />
      <Card.Header>Bio:</Card.Header><div>{data.user.about}</div>
    </Card.Content>
    <Card.Content extra>

    </Card.Content>

    <Modal trigger={<Button>Edit Profile</Button>}>
    <Header>Edit Profile</Header>
    <Modal.Content image>
    <Image wrapped size='medium' src='https://react.semantic-ui.com/images/avatar/large/rachel.png' />
    <br></br>
    <Button basic color='orange' content='Upload Image' />
    <br></br>
      <Modal.Description>
        <Input placeholder='First Name'></Input>
        <Input placeholder='Last Name'></Input>
        <br></br>
        <Input placeholder='Username'></Input>
        <Input placeholder='Email'></Input>
        <br></br>
        <TextArea placeholder='Short Bio:'></TextArea>
        <br></br>
        <br></br>
        <Button basic color='teal' content='Update profile' />
        <Button basic color='teal' content='Logout' />
        <br></br>
        {/* We need this page to refresh with new data */}
        {/* <Link to="/profile">
                  <br></br>
                  <Link to="/profile"></Link>
            <Button content='Login' primary type="submit" /></Link> */}
      </Modal.Description>
    </Modal.Content>
  </Modal>
  </Card>
      // <React.Fragment>
      //   <h2>{data.user.username}*</h2>
      //   <h3>{data.user.displayName}*</h3>
      //   <p> About:{data.user.about}*</p>
      //   <p> {data.user.messages}</p>
      // </React.Fragment>
    );
  }
}

export default UserProfile;
