import React, { Component } from "react";
import { connect } from "react-redux";
import { loginThenGoToUserProfile as login } from "../actions";
// import Spinner from "react-spinkit";

import { Card, Image, Input, Header, Button, Form, Modal, Checkbox } from "semantic-ui-react";
import { Link } from "react-router-dom";
import picture from "../Images/favicon.ico";



class LoginForm extends Component {
  state = { username: "", password: "" };

  handleLogin = e => {
    e.preventDefault();
    this.props.login(this.state);
  };

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { isLoading } = this.props;
    return (
      <Form centered='true'>
        <Header color='orange' textAlign='centered' as='h1'>Kwitter</Header>
        {/* <Card.Header>Kwitter</Card.Header>  */}
        <Image centered='true' src={picture} />
        <Card.Content centered= "true">
          <Card.Meta>
            <div>Login</div>
            <Form onSubmit={this.handleLogin}>
              <label htmlFor="username"></label>
              <Input
                type="text"
                name="username"
                placeholder="username"
                autoFocus
                required
                onChange={this.handleChange}
              />
              <label htmlFor="password"></label>
              <Input
                type="password"
                name="password"
                placeholder="password"
                required
                onChange={this.handleChange}
              /></Form>
          </Card.Meta>
          <Card.Description>New to Kwitter? Create an account!</Card.Description>
        </Card.Content>
        <div>
          <Link to="/profile">
            <Button content='Login' primary type="submit" disabled={isLoading} /></Link>
          <Modal trigger={<Button>New Account</Button>}>
            <Modal.Header>Create New Account</Modal.Header>
            <Modal.Content image>
              <Image src={picture} />
              <Modal.Description>
                <Header>Let's get you signed up!</Header>
                <br></br>
                <Input placeholder='First Name'></Input>
                <Input placeholder='Last Name'></Input>
                <br></br>
                <Input placeholder='Email'></Input>
                <Input placeholder='Username'></Input>
                <Link to="/profile">
                  <br></br>
                  <Link to="/profile"></Link>
            <Button content='Login' primary type="Submit" disabled={isLoading} /></Link>
                  <br></br>
              </Modal.Description>
            </Modal.Content>
          </Modal>
        </div>
        <Card.Content extra>
        </Card.Content>
      </Form>
    );
  }
}

export default connect(
  ({ auth }) => ({
    isLoading: auth.loginLoading,
    err: auth.loginError
  }),
  { login }
)(LoginForm);
