import { GET_USER, GET_USER_SUCCESS, GET_USER_FAIL, Get } from "../actions";

const initialState = {
  userData: null,
  displayName:"",
  userId:""
};

export default (state = initialState, action) => {
  switch (action.type) {
    case GET_USER:
      return { ...state };

    case GET_USER_SUCCESS:
      return {
        ...state,
        userData: action.payload,
        displayName: action.payload.displayName,
        userId: action.payload.id
      };

    case GET_USER_FAIL:
      return { ...state };

    default:
      return state;
  }
};
