import React, { Component } from "react";
import { connect } from "react-redux";
import { loginThenGoToUserProfile as login } from "../actions";
// import Spinner from "react-spinkit";

import { Card, Image, Input, Header, Button, Form } from "semantic-ui-react";
import { Link } from "react-router-dom";
import picture from "../Images/favicon.ico";



class LoginForm extends Component {
  state = { username: "", password: "" };

  handleLogin = e => {
    e.preventDefault();
    this.props.login(this.state);
  };

  handleChange = e => {
    this.setState({ [e.target.name]: e.target.value });
  };

  render() {
    const { isLoading, } = this.props;
    return (
      <Card centered= 'true'> 
       <Header textAlign='centered' as='h1'>Kwitter</Header>
      {/* <Card.Header>Kwitter</Card.Header>  */}
    <Image src= {picture} />
    <Card.Content>
      <Card.Meta>
        <div>Login</div>
        <Form onSubmit={this.handleLogin}>
         <label htmlFor="username"></label>
         <Input
            type="text"
            name="username"
            placeholder="username"
            autoFocus
            required
            onChange={this.handleChange}
          />
          <label htmlFor="password"></label>
          <Input 
            type="password"
            name="password"
            placeholder="password"
            required
            onChange={this.handleChange}
          /></Form>
      </Card.Meta>
      <Card.Description>Matthew is a musician living in Nashville.</Card.Description>
    </Card.Content>
    {/* <Button type="submit" disabled={isLoading}>
            Login
       </Button> */}
       <div>
         <Link to ="/profile">
    <Button content='Login' primary type="submit" disabled={isLoading}/></Link>
    <Button content='Sign up' secondary />
    </div>
    <Card.Content extra>

    </Card.Content>
  </Card>
      // <React.Fragment>
      //   <h1>Login</h1>
      //   <form onSubmit={this.handleLogin}>
      //     <label htmlFor="username">Username</label>
      //     <input
      //       type="text"
      //       name="username"
      //       autoFocus
      //       required
      //       onChange={this.handleChange}
      //     /><br></br>
      //     <label htmlFor="password">Password</label>
      //     <input
      //       type="password"
      //       name="password"
      //       required
      //       onChange={this.handleChange}
      //     /><br></br>
      //     <button type="submit" disabled={isLoading}>
      //       Login
      //     </button>
      //   </form>
      //   {isLoading && <Spinner name="circle" color="blue" />}
      //   {err && <p style={{ color: "red" }}>{err}</p>}
      // </React.Fragment>
    );
  }
}

export default connect(
  ({ auth }) => ({
    isLoading: auth.loginLoading,
    err: auth.loginError
  }),
  { login }
)(LoginForm);
