import React, { Component } from "react";
import {
  Card,
  Image,
  Modal,
  Form,
  Grid,
  Header,
  Icon,
  Segment,

} from "semantic-ui-react";
import { connect } from "react-redux";
import { updateUser } from "../Actions/actions.js";
import DeleteUser from "./DeleteUser";

class EditProfile extends Component {
  state = { displayName: "", password: "", about: "" };

  handleChange = (e, { value }) => this.setState({ [e.target.name]: value });
  handleSubmit = (e, { value }) => {
    this.props.updateUser({ ...this.state });
  };

  render() {
    return (
      <React.Fragment>
        <Grid columns={2} relaxed="very">
          <Grid.Column>
            <Card>
              <Image wrapped size="medium" src={this.props.userImageSrc} />
              <Card.Content>
                <Card.Header>{this.props.displayName}</Card.Header>
                <Card.Meta>
                  <span className="displayName">{this.props.displayName}</span>
                </Card.Meta>
                <Card.Header>Bio:{this.props.about}</Card.Header>
                <div />
              </Card.Content>
              {/* <Card.Content extra /> */}
              <Modal trigger={<Button>Edit Profile</Button>}>
                <Header>Profile</Header>
                <Modal.Content image>
                  <Image wrapped size="medium" src={this.props.userImageSrc} />
                  <br />
                  <Form onSubmit={this.handleUploadImage}>
                    <input type="file" name="picture" />
                    <br />
                    <Modal.Description>
                      <Input placeholder="Username" />
                      <Input placeholder="Displayame" />
                      <br />
                      <Input
                        type="email"
                        name="email"
                        placeholder="Em@il"
                        required
                        onChange={this.handleChange}
                      />
                      <Input placeholder="Password" />
                      <br />
                      <TextArea placeholder="Short Bio:" />
                      <br />
                      <br />
                      <Button basic color="teal" content="Update profile" />
                      <button
                        onClick={() => {
                          this.props.logout();
                        }}
                      >
                        Logout
                      </button>
                      <button
                        onClick={() => {
                          this.props.deleteUser();
                        }}
                      >
                        Delete User
                      </button>

                      <br />
                      {/* We need this page to refresh with new data */}
                      {/* <Link to="/profile">
                  <br></br>
                  <Link to="/profile"></Link>
            <Button content='Login' primary type="submit" /></Link> */}
                    </Modal.Description>
                  </Form>
                </Modal.Content>
              </Modal>
              <Link to="/timeline">
                <Button color='green'>
                  Return to timeline </Button>
              </Link>
            </Card>
            <button
              onClick={() => {
                this.props.logout();
              }}
            >
              Logout
             </button>
          </Grid.Column>
          <Grid.Column>
            <UserMessages/>
          </Grid.Column>
        </Grid>
      </React.Fragment>

    );
  }
}

const mapDispatchToProps = dispatch => {
  return {
    updateUser: userData => dispatch(updateUser(userData))

  };
};
const mapStateToProps = state => {
  return {
    token: state.authentication.token
  };
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(EditProfile);
