import React, { Component } from "react";
import { connect } from "react-redux";
import {logout} from "../actions";
import {getUserById} from "../actions"
import {
  Card,
  Icon,
  Image,
  Button,
  Modal,
  Header,
  Input,
  TextArea,
  Segment
} from "semantic-ui-react";
import ComposeMessage from "./ComposeMessage";
import UserMessages from "./UserMessages";
// import { Link } from "react-router-dom";
// import { connect } from "react-redux";

// const data = {
//   user: {
//     id: 498,
//     username: "SD",
//     displayName: "Salvador D",
//     about: "Located in Indy.",
//     createdAt: "2019-03-12T16:01:39.622Z",
//     updatedAt: "2019-03-12T16:01:39.622Z",
//     messages: []
//   }
// };
class UserProfile extends Component {
  render() {
    console.log()
    return (
      <Card centered="true">
        <Image
          wrapped
          size="medium"
          src="https://react.semantic-ui.com/images/avatar/large/matthew.png"
        />
        <Card.Content>
          <Card.Header>{this.props.user.displayName}</Card.Header>
          <Card.Meta>
            <span className="displayName">{this.props.user.displayName}</span>
          </Card.Meta>
          <Icon name="drivers license" />
          <Card.Header>Bio:</Card.Header>
          <div></div>
        </Card.Content>
        {/* <Card.Content extra /> */}


        <Modal trigger={<Button>Edit Profile</Button>}>
          <Header>Profile</Header>
          <Modal.Content image>
            <Image
              wrapped
              size="medium"
              src="https://react.semantic-ui.com/images/avatar/large/rachel.png"
            />
            <br />
            <Button basic color="orange" content="Upload Image" />
            <br />
            <Modal.Description>
              <Input placeholder="Username" />
              <Input placeholder="Displayame" />
              <br />
              <Input placeholder="Email" />
              <Input placeholder="Password" />
              <br />
              <TextArea placeholder="Short Bio:" />
              <br />
              <br />
              <Button basic color="teal" content="Update profile" />
              <Button basic color="teal" content="Logout" />
              <Button basic color="red" content="Delete user" />

              <br />
              {/* We need this page to refresh with new data */}
              {/* <Link to="/profile">
                  <br></br>
                  <Link to="/profile"></Link>
            <Button content='Login' primary type="submit" /></Link> */}

            </Modal.Description>
          </Modal.Content>
        </Modal>


        <Modal trigger={<Button>My Kweets</Button>}>
          <Modal.Header>My Kweets</Modal.Header>

          <Modal.Content image>
            <Image
              wrapped
              size="medium"
              src="https://react.semantic-ui.com/images/avatar/large/matthew.png"
            />
            {/* user messages will populate down below */}
            <Modal.Description>
              <Header>Modal Header</Header>
              <p>
                This is an example of expanded content that will cause the
                modal's dimmer to scroll
              </p>
              {/* <Segment vertical>
                <ComposeMessage />
              </Segment>
              <Segment vertical>
                <UserMessages />
              </Segment> */}
            </Modal.Description>
          </Modal.Content>
          <Modal.Actions>
            <Button primary>
              Proceed <Icon name="right chevron" />
            </Button>
          </Modal.Actions>
        </Modal> <button onClick={() => {this.props.logout()}}>logout</button>
      </Card>

      // <React.Fragment>
      //   <h2>{data.user.username}*</h2>
      //   <h3>{data.user.displayName}*</h3>
      //   <p> About:{data.user.about}*</p>
      //   <p> {data.user.messages}</p>
      // </React.Fragment>
    );
  }
}
// export default connect(
//   ({ auth }) => ({
//     userId: auth.login.id
//   }),
//    null 
// )(UserProfile);


// const mapStateToProps = state => {
//   return {
//     user: state.user
//   }
// }

export default connect(
  ({ auth,users }) => ({
   user:auth.user,
   displayName: users.displayName,

  }),
  {logout}
)(UserProfile);
