import { domain} from "./constants";

//action types
export const GET_USER = "GET_USER";
export const GET_USER_SUCCESS = "GET_USER_SUCCESS";
export const GET_USER_FAIL = "GET_USER_FAIL";

const url = domain + "/users";

//action creators
const getUserById = (id) => dispatch => {
dispatch({
    type: GET_USER
})

return fetch(url + `/${id}`)
.then(res=>res.json())
}
