import { GET_USER, GET_USER_SUCCESS, GET_USER_FAIL } from "../actions";
const initialState = {
  user
};

export default (state = initialState, action) => {
  switch (action.type) {
    case GET_USER:
    return{...state}

    case GET_USER_SUCCESS:
    return{
      ...state,
      userData:action.payload
    }

    case GET_USER_FAIL:
    return{...state}

    default:
      return state;
  }
};
