import React, { Component } from "react";
import { Card, Icon, Image, Button, Modal, Header,Input } from 'semantic-ui-react'

const data = {
  user: {
    id: 498,
    username: "salvador",
    displayName: "test",
    about: "",
    createdAt: "2019-03-12T16:01:39.622Z",
    updatedAt: "2019-03-12T16:01:39.622Z",
    messages: []
  }
};
class UserProfile extends Component {
  render() {
    return (
      <Card centered= 'true'>
      <Button>Logout</Button>
    <Image src='https://react.semantic-ui.com/images/avatar/large/matthew.png' />
    <Card.Content>
      <Card.Header>{data.user.username}</Card.Header>
      <Card.Meta>
        <span className='Username'>{data.user.displayName}</span>
      </Card.Meta>
      <Card.Description>Bio:{data.user.about}</Card.Description>
    </Card.Content>
    <Card.Content extra>
      <div>
        <Icon name='user' />
        {data.user.messages}
      </div>
    </Card.Content>
    <Modal trigger={<Button>Edit profile</Button>}>
    <Modal.Header>Select a Photo</Modal.Header>
    <Modal.Content image>
      <Image wrapped size='medium' src='https://react.semantic-ui.com/images/avatar/large/rachel.png' />
      <Modal.Description>
        <Input placeholder='test'></Input>
        <TextArea>placehol</TextArea>
      </Modal.Description>
    </Modal.Content>
  </Modal>
  </Card>
      // <React.Fragment>
      //   <h2>{data.user.username}*</h2>
      //   <h3>{data.user.displayName}*</h3>
      //   <p> About:{data.user.about}*</p>
      //   <p> {data.user.messages}</p>
      // </React.Fragment>
    );
  }
}

export default UserProfile;
