import React, { Component } from "react";
import { connect } from "react-redux";
import { logout } from "../actions";
import { getUsers, getLoggedInUser, uploadUserImage } from "../actions";
import {
  Card,
  Icon,
  Image,
  Button,
  Modal,
  Header,
  Input,
  TextArea,
  Grid,
  Form, 
} from "semantic-ui-react";
import UserMessages from "./UserMessages";
import { Link } from "react-router-dom";

class UserProfile extends Component {
  componentDidMount() {
    this.props.getUsers();
    this.props.getLoggedInUser();
  }
  handleUploadImage = event => {
    event.preventDefault();
    const formData = new FormData(event.target);
    this.props.uploadUserImage(formData);
  };
  render() {
    // console.log(this.props.user)
    return (
      <React.Fragment>
        <Grid columns={2} relaxed="very">
          <Grid.Column>
            <Card>
              <Image wrapped size="medium" src={this.props.userImageSrc} />
              <Card.Content>
                <Card.Header>{this.props.displayName}</Card.Header>
                <Card.Meta>
                  <span className="displayName">{this.props.displayName}</span>
                </Card.Meta>
                <Icon name="drivers license" />
                <Card.Header>Bio:{this.props.about}</Card.Header>
                <div />
              </Card.Content>
              {/* <Card.Content extra /> */}
              <Modal trigger={<Button>Edit Profile</Button>}>
                <Header>Profile</Header>
                <Modal.Content image>
                  <Image wrapped size="medium" src={this.props.userImageSrc} />
                  <br />
                  <Form onSubmit={this.handleUploadImage}>
                    <input type="file" name="picture" />
                    <br />
                    <Modal.Description>
                      <Input placeholder="Username" />
                      <Input placeholder="Displayame" />
                      <br />
                      <Input placeholder="Email" />
                      <Input placeholder="Password" />
                      <br />
                      <TextArea placeholder="Short Bio:" />
                      <br />
                      <br />
                      <Button basic color="teal" content="Update profile" />
                      <Button basic color="teal" content="Logout" />
                      <Button basic color="red" content="Delete user" />

                      <br />
                      {/* We need this page to refresh with new data */}
                      {/* <Link to="/profile">
                  <br></br>
                  <Link to="/profile"></Link>
            <Button content='Login' primary type="submit" /></Link> */}
                    </Modal.Description>
                  </Form>
                </Modal.Content>
              </Modal>
              <Link to="/timeline">
              <Button color='green'>
              Return to timeline </Button>
              </Link>
            </Card>
            <button
               onClick={() => {
                 this.props.logout();
               }}
             >
               Logout
             </button>
          </Grid.Column>
        </Grid>
      </React.Fragment>
    );
  }
}

const mapStateToProps = state => {
  const { username, displayName, about } = state.users.userData;
  const userImageSrc = state.users.userImage;
  return {
    username,
    displayName,
    about,
    userImageSrc
  };
};
const mapDispatchToProps = {
  uploadUserImage,
  logout,
  getUsers,
  getLoggedInUser
};
export default connect(
  mapStateToProps,
  mapDispatchToProps
)(UserProfile);
